go.calculator:
package main

import (
	"fmt"
)

func calculator(a float64, b float64, operation string) string {
	switch operation {
	case "+":
		return fmt.Sprintf("%.2f", a+b)
	case "-":
		return fmt.Sprintf("%.2f", a-b)
	case "*":
		return fmt.Sprintf("%.2f", a*b)
	case "/":
		if b != 0 {
			return fmt.Sprintf("%.2f", a/b)
		}
		return "Деление на ноль"
	default:
		return "Неизвестная операция"
	}
}

func main() {
	fmt.Println(calculator(10, 5, "+")) // Пример: 10 + 5 = 15
}

go.check_io:
package main

import (
	"os"
)

func main() {
	for _, arg := range os.Args {
		os.Stdout.WriteString("arg: " + arg + "\n")
	}

	for _, env := range os.Environ() {
		os.Stdout.WriteString("env: " + env + "\n")
	}

	buf := make([]byte, 1024)
    n, _ := os.Stdin.Read(buf)
    if n > 0 {
        os.Stdout.WriteString("from stdin: ")
        os.Stdout.Write(buf[:n])
        os.Stdout.WriteString("\n")
    }

    os.Stdout.WriteString("stdout message!\n")
    os.Stderr.WriteString("stderr message!\n")

	code := 0
	if len(os.Args) > 1 {
		code = 42
	}
	os.Exit(code)
}

